import java.util.*;
 public class average
 {
 public static void main(String[] args) {
 Scanner ob = new Scanner(System.in);
 System.out.println("Enter Marks for you eligible subject to choose");
 System.out.print("Maths = ");
 float maths = ob.nextFloat();
 System.out.print("English = ");
 float eng = ob.nextFloat();
 System.out.print("Biology = ");
 float bio = ob.nextFloat();
 System.out.print("Physics = ");
 float physics = ob.nextFloat();
 System.out.print("Chemistry = ");
 float chem = ob.nextFloat();
 float avg=(maths+chem+eng+bio+physics)/5;
 System.out.println("Average is "+avg+", Percentage is "+avg+" ");
 if (avg>=80)
 {
 System.out.println("You are eligible for Computer Science");
 }
 else
 {
 System.out.println("You are eligible for Biology");
 }
 }}

import java.util.*;
 public class celsius
 {
 public static void main(String[] args) {
 Scanner ob = new Scanner(System.in);
 System.out.print("Enter Temperature in Celsius= ");
 float cel = ob.nextFloat();
 float fah=(cel*9/5)+32;
 if (fah>98.6) {
 System.out.println("You Having Fever! "+fah+",Go to Doctor or take medicine and rest. ");
 }
 else
 {
 System.out.println("Temperature is Normal "+fah);
 }
 } }

import java.util.*;
 public class diff {
 public static void main(String[] args) {
 Scanner object = new Scanner(System.in);
 System.out.println("Enter The First Number: ");
 // here i am taking the value from the user
 int n1 = object.nextInt();
 System.out.println("Enter The Second Number: ");
 int n2 = object.nextInt();
 int sum, diff, prod;
 sum = n1 + n2;
 if (n1 > n2) {

diff = n1 - n2;
 } else {
 diff = n2 - n1;
 }
 prod = n1 * n2;
 // here i am printing all the results
 System.out.println("Sum of " + n1 + " and " + n2 + " is- " + sum);
 System.out.println("Difference of " + n1 + " and " + n2 + " is- " + diff);
 System.out.println("Product of " + n1 + " and " + n2 + " is- " + prod);
 }}

import java.util.*; 
 public class evening 
 {
 public static void main(String[] args) {
 int i, a = 0, c = 0, n, avg;
 Scanner ob = new Scanner(System.in); 
 System.out.println("Enter number: "); 
 n = ob.nextInt();
 for (i = 1; i <=n; i++){ 
 if (i % 2 == 0) { 
 a = a + i; 
 c = c + 1; 
 } 
 } 
 avg = a / c; 
 System.out.println("Sum: "+a+" number of time: "+c); 
 System.out.println("The Average of " + n + " Event Number: " + avg); 
 }}

import java.util.*;
 public class factorial
 {
 public static void main(String[] args)
 {
 int i,fact=1,n;
 Scanner ob=new Scanner(System.in);
 System.out.print("Enter The Number: ");
 n=ob.nextInt();
 for (i=1;i<=n;i++)
 {
 fact=fact*i;
 }
 System.out.println("Factorial of "+n+" is "+fact);
 }}

