System.out.println(\"Enter Second Number \");\n
int n2 = ob.nextInt();\n
System.out.println(\"Enter Third Number \");\n
int n3 = ob.nextInt();\n
if (n1 > n2 && n1 > n3) {\n
System.out.println(n1 + \"is grater\");\n
} else if (n2 > n3) {\n
System.out.println(n2 + \"is greater\");\n
} else if (n3 > n1 && n3 > n2) {\n
System.out.println(n3 + \"is greater\");\n
} else {\n
System.out.println(\"All are equal\");\n
}}
}

import java.util.*;
 public class Gross_Salary
 {
 public static void main(String[] args)
 {
 Scanner ob = new Scanner(System.in);
 System.out.println("Enter You Salary");
 int sal = ob.nextInt();
 int da=sal*20/100;
 int hra=sal*10/100;
 int cta=sal*12/100;
 int gs=sal+da+hra+cta;
 int ad=gs-sal;
 System.out.print(sal);
 System.out.print(" + ");
 System.out.print(ad);
 //additional Charges
 System.out.print(" = "+gs);
 }}

import java.util.*;
 public class market {
 public static void main(String[] args) {
 Scanner ob = new Scanner(System.in);
 System.out.println("Enter Customer Name: ");
 String name = ob.next();
 System.out.print("Enter Total Cost= ");
 double tc = ob.nextDouble();
 double dis=0;
 if (tc<=2000) {
 dis=(tc*0.05);
 }
 else if(tc>2000&&tc<=5000)
 {
 dis=(tc*0.1);
 }
 else if(tc>5000&&tc<=10000)
 {
 dis=(tc*0.15);
 }
 else if(tc<10000)
 {
 dis=(tc*0.2);
 }
 double afterd=tc-dis;
 System.out.println(name);
 System.out.println("After Discount= "+afterd);
 }}

