import java.util.*;
 public class sellingprice
 {
 public static void main(String[] args) {
 Scanner ob = new Scanner(System.in);
 System.out.print("Enter Market price= ");
 int mp = ob.nextInt();
 int disp=10+20-(10*20)/100;
 System.out.println(disp);
 int dis=mp*disp/100;
 int sp=mp-dis;
 System.out.println("Selling price= "+sp);
 }}

import java.util.*;
 public class square
 {
 public static void main(String[] args) {
 int n1,n2,sq,cu;
 Scanner ob=new Scanner(System.in);
 System.out.print("Enter the First number: ");
 n1=ob.nextInt();
 System.out.print("Enter the Second number: ");
 n2=ob.nextInt();
 if(n1>n2){
 sq=n1*n1;
 cu=n2*n2*n2;
 System.out.println("Square of "+n1+": "+sq+", Cube of "+n2+", "+cu);
 }
 else if(n2>n1){
 sq=n2*n2;
 cu=n1*n1*n1;
 System.out.println("Square of "+n2+": "+sq+", Cube of "+n1+", "+cu);
 }
 else
 {
 System.out.println("Both are Equal");
 }
 }}
